package inProgress;

import java.util.ArrayList;

public class LargestSeriesProduct08 {

	public static void main(String[] args) {
		String orig = "";
		
		ArrayList<Integer> series = new ArrayList<Integer>(0);
		
		for (int i = 0;i < 1000; i++) {
			int newTerm = Integer.parseInt(orig.substring(i, i + 1));
			
			series.add(newTerm);
			System.out.println(series.get(i));
		}
		
		long largest = Long.MIN_VALUE;
		
		for (int i = 0;i < 987; i++) {
			long product = series.get(i) * series.get(i+1) * series.get(i+2) * series.get(i+3) * series.get(i+4) * series.get(i+5) * series.get(i+6) * series.get(i+7) * series.get(i+8) * series.get(i+9) * series.get(i+10) * series.get(i+11) * series.get(i+12);
			
			if (product > largest) {
				largest = product;
			}
		}
		System.out.println(largest);
	}

}
